function DateChunks(year, month, date) {

    gwn = (year, monthNo, date) => {
        let sd = (new Date(year, monthNo - 1, 1)).getDay(),
            nod = new Date(year, monthNo, 0).getDate(),
            wn = 0,
            d = 1;

        if (date < sd || date > nod || monthNo < 1 || monthNo > 12) {
            throw Error('invalid params')
        }

        for (let i = 0; i <= 41; i++) {
            if (i % 7 === 0) wn++;
            if (i >= sd && d <= nod) {
                if (date === d) {
                    return wn
                }
                d++
            }
        }
    }
    let wn = gwn(year, month, date);
    let mc = (p) => Math.ceil(p);

    return {
        date: date,
        year: year,

        week: {
            no: wn,
        },

        month: {
            no: month,
        },

        quarter: {
            no: mc(month / 3),
        },

        halfYear: {
            no: mc(month / 6),
        }
    }
}
