const mfn = [, "January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
const msn = [, "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"]
const dn = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"]
const nth = ['Zeroth', 'First', 'Second', 'Third', 'Fourth', 'Fifth', 'Sixth']


function DateChunks(year, month, date) {

    gwn = (year, monthNo, date) => {
        let sd = new Date(year, monthNo - 1, 1).getDay(),
            nod = new Date(year, monthNo, 0).getDate(),
            wn = 0,
            d = 1;

        if (date < sd || date > nod || monthNo < 1 || monthNo > 12) {
            throw Error('invalid params')
        }

        for (let i = 0; i <= 41; i++) {
            if (i % 7 === 0) wn++;
            if (i >= sd && d <= nod) {
                if (date === d) {
                    return wn
                }
                d++
            }
        }
    }

    let wn = gwn(year, month, date);
    let mc = (p) => Math.ceil(p);

    return {
        date: date,
        year: year,

        week: {
            no: wn,
            name: nth[wn] + " Week"
        },

        month: {
            no: month,
            fullname: mfn[month],
            shortname: msn[month],
        },

        quarter: {
            no: mc((month) / 3),
            name: nth[mc(month / 3)] + " Quarter",
        },

        halfYear: {
            no: mc(month / 6),
            name: nth[mc(month / 6)] + " Half",
        }
    }
}
